import React, { useState } from 'react';
import ReactDOM from 'react-dom/client';

const NumberButton = () => {

}

const ClearButton = (props) => {

  const handleClick = () => {
    props.setCalculatedAnswer("0");
    props.setExpression("");
    props.setNextElement("");
  }

  return (
    <button className='clear button' id={props.id} onClick={handleClick} type='button'>AC</button>
  )
}

const OperandButton = (props) => {

  const handleClick = () => {

  }

  return(
    <button id={props.id} onClick={handleClick} type='button'>{props.value}</button>
  )
}

const EqualButton = () => {

}

const Display = (props) => {
  return (
    <div id={props.id} className='display'>
      <div className='expressionDisplay'>
        {props.expression}{props.nextElement}
      </div>
      <div className='calculatedDisplay'>
        {props.calculatedAnswer}
      </div>
    </div>
  )
}

const App = () => {
  const [expression, setExpression] = useState("0");
  const [nextElement, setNextElement] = useState("");
  const [calculatedAnswer, setCalculatedAnswer] = useState("0");

  return(
    <div id='calculator'>
      <Display id='display' expression={expression} nextElement={nextElement} calculatedAnswer={calculatedAnswer}/>
      <ClearButton id='clear' setExpression={setExpression} setNextElement={setNextElement} setCalculatedAnswer={setCalculatedAnswer}/>
      <OperandButton id='multiply' value='*' expression={expression} nextElement={nextElement}/>
    </div>
  )
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <App />
);
